<?php
// Define target directory
$target_dir = "uploads/";
$model = '';

// Get uploaded image information
$image = $_FILES["image"];
$decodeVal = "";

// Check if image was uploaded
if (isset($image)) {
    $fileName = basename($image["name"]);
    $targetFilePath = $target_dir . $fileName;
    $fileType = strtolower(pathinfo($targetFilePath,PATHINFO_EXTENSION));

    // Allow certain file formats
    $allowTypes = array("svg");

    if (in_array($fileType, $allowTypes)) {
        // Check for upload errors
        if ($image["error"] === UPLOAD_ERR_OK) {
            // Upload image
            if (move_uploaded_file($image["tmp_name"], $targetFilePath)) {
                echo "Image uploaded successfully!";
                $decodeVal = solve_captcha($targetFilePath, $model);
            } else {
                echo "Failed to upload image.";
            }
        } else {
            // Display error message based on upload error code
            echo "Error uploading image: " . $image["error"];
        }
    } else {
        echo "Only SVG files are allowed.";
    }
} else {
    echo "Please select an image file.";
}

// Display uploaded image if successful
if (isset($fileName)) {
    echo "<br><br>Uploaded Image:";
    echo "<br><img src='$target_dir/$fileName' alt='$fileName'>";
    echo "<br>Image Name: $fileName";
    echo "<br>Decoded Captcha text: $decodeVal";
}


function solve_captcha($svgCaptchaFile, $model) {
    $svgCaptcha = file_get_contents($svgCaptchaFile);
    $parsedModel = json_decode(base64_decode($model), true);

    // Use DOMDocument for parsing (more robust than minidom)
    $dom = new DOMDocument();
    $dom->loadXML($svgCaptcha);

    // Get paths with no stroke attribute
    $paths = $dom->getElementsByTagName("path");
    $nostrokePaths = [];
    foreach ($paths as $path) {
        if (!$path->hasAttribute("stroke")) {
            $nostrokePaths[] = $path;
        }
    }

    // Extract values from paths
    $vals = array_map(function($path) {
        $dAttribute = $path->getAttribute('d');
        return (int) preg_replace('/[^0-9.]/', '', explode('.', $dAttribute)[0]);
    }, $nostrokePaths);
    // Sort values and create solution array
    $sortedVals = $vals;
    sort($sortedVals);
    $solutionLen = count($sortedVals);
    $solution = array_fill(0, $solutionLen, '');

    // Match patterns and assign values to solution
    foreach ($nostrokePaths as $i => $path) {
        $pattern = preg_replace("/[\d.\s-]/", '', $path->getAttribute('d'));
        $index = array_search($vals[$i], $sortedVals);
        $solution[$index]= isset($parsedModel[$pattern]) ? $parsedModel[$pattern] : '';
    }

    // Return the solved CAPTCHA string
    return implode('', $solution);
}
